import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { fetchCategories } from "../../redux/slices/categoriesSlice";
import PaginatedTableCategoriesComponent from "../../components/TableComponents/PaginatedTableCategoriesComponent";
import "../../assets/styles/PopupAdmin.css";
const Categories = () => {
  const categories = useSelector((state) => state.categories.categories);
  const products = useSelector((state) => state.products.products);
  const [isShowPopupAdd, setIsShowPopupAdd] = useState(false);
  const [filterCategories, setFilterCategories] = useState(products);
  const [keyWordSearch, setKeyWordSearch] = useState("");
  useEffect(() => {
    setFilterProducts(products);
  }, [products]);
  const handleSearch = (event) => {
    setKeyWordSearch(event.target.value);
    const filtered = products.filter((product) =>
      product.nameProduct
        .toLowerCase()
        .includes(event.target.value.toLowerCase())
    );
    setFilterProducts(filtered);
  };
  const hanleTogglePopupAdd = (e) => {
    isShowPopupAdd ? setIsShowPopupAdd(false) : setIsShowPopupAdd(true);
  };

  return (
    <>
      <div className="w-full px-6 py-6 mx-auto flex flex-col gap-4">
        <div className="w-full h-auto flex justify-between items-center gap-3">
          <div className="text-xl font-bold pt-2 text">
            <h6>Danh sách danh mục</h6>
          </div>
          <div className="flex gap-3 items-center">
            <div class="relative flex flex-wrap items-stretch w-[250px] transition-all rounded-lg ease-soft h-[42px]">
              <span class="text-sm ease-soft leading-5.6 absolute z-50 -ml-px flex h-full items-center whitespace-nowrap rounded-lg rounded-tr-none rounded-br-none border border-r-0 border-transparent bg-transparent py-2 px-2.5 text-center font-normal text-slate-500 transition-all">
                <ion-icon name="search-outline"></ion-icon>
              </span>
              <input
                type="text"
                class="pl-8.75 text-sm focus:shadow-soft-primary-outline ease-soft w-1/100 leading-5.6 relative -ml-px block min-w-0 flex-auto rounded-lg border border-solid border-gray-300 bg-white bg-clip-padding pr-3 text-gray-700 transition-all placeholder:text-gray-500 focus:border-fuchsia-300 focus:outline-none focus:transition-shadow pl-7"
                placeholder="Type here..."
                fdprocessedid="0v423v"
              />
            </div>
            <button
              onClick={hanleTogglePopupAdd}
              white-style-btn=""
              class="inline-block w-[150px] px-4 py-3 font-bold text-center uppercase align-middle transition-all bg-transparent border border-solid rounded-lg cursor-pointer xl-max:cursor-not-allowed xl-max:opacity-65 xl-max:pointer-events-none xl-max:bg-gradient-to-tl xl-max:from-purple-700 xl-max:to-pink-500 xl-max:text-white xl-max:border-0 hover:scale-102 hover:shadow-soft-xs active:opacity-85 leading-pro text-xs ease-soft-in tracking-tight-soft shadow-soft-md bg-150 bg-x-25 border-fuchsia-500 bg-none text-fuchsia-500 hover:border-fuchsia-500"
              data-class="bg-white"
              fdprocessedid="0qszhi"
            >
              Thêm danh mục
            </button>
          </div>
        </div>
        <div className="flex flex-wrap -mx-3">
          <div className="flex-none w-full max-w-full px-3">
            <div className="relative flex flex-col min-w-0 mb-6 break-words bg-white border-0 border-transparent border-solid shadow-soft-xl rounded-2xl bg-clip-border">
              <div className="flex-auto px-0 pt-0 pb-2">
                <div className="p-0 overflow-x-auto ps">
                  <PaginatedTableCategoriesComponent></PaginatedTableCategoriesComponent>
                  <table className="items-center w-full mb-0 align-top border-gray-200 text-slate-900">
                    <thead className="align-bottom">
                      <tr>
                        <th className="px-6 py-3 font-bold text-left uppercase align-middle bg-transparent border-b border-gray-200 shadow-none text-xxs border-b-solid tracking-none whitespace-nowrap text-slate-400 opacity-100">
                          ID
                        </th>
                        <th className="px-6 py-3 pl-2 font-bold text-left uppercase align-middle bg-transparent border-b border-gray-200 shadow-none text-xxs border-b-solid tracking-none whitespace-nowrap text-slate-400 opacity-100">
                          Thông tin chung
                        </th>
                        <th className="px-6 py-3 font-bold text-center uppercase align-middle bg-transparent border-b border-gray-200 shadow-none text-xxs border-b-solid tracking-none whitespace-nowrap text-slate-400 opacity-100">
                          Tên danh mục
                        </th>
                        <th className="px-6 py-3 font-bold text-center uppercase align-middle bg-transparent border-b border-gray-200 shadow-none text-xxs border-b-solid tracking-none whitespace-nowrap text-slate-400 opacity-100">
                          Mô tả
                        </th>
                        <th className="px-6 py-3 font-bold text-center uppercase align-middle bg-transparent border-b border-gray-200 shadow-none text-xxs border-b-solid tracking-none whitespace-nowrap text-slate-400 opacity-100">
                          thao tác
                        </th>
                      </tr>
                    </thead>
                    <tbody>
                      {categories.map((category) => (
                        <tr key={category.id}>
                          <td className="p-2 px-6 align-middle bg-transparent border-b whitespace-nowrap shadow-transparent">
                            <h6 className="mb-0 text-sm leading-normal">
                              #{category.id}
                            </h6>
                          </td>
                          <td className="p-2 align-middle bg-transparent border-b whitespace-nowrap shadow-transparent">
                            <div className="flex px-2 py-1">
                              <div>
                                <img
                                  src={category.imageCategory}
                                  className="inline-flex items-center justify-center mr-4 text-sm text-white transition-all duration-200 ease-soft-in-out h-16 w-16 rounded-xl"
                                  alt="user1"
                                />
                              </div>
                              <div className="flex flex-col justify-center">
                                <h6 className="mb-0 text-[15px] leading-normal">
                                  {category.nameCategory}
                                </h6>
                                <p className="mb-0 text-[14px] leading-tight text-slate-500">
                                  Hiện có{" "}
                                  {products.reduce(
                                    (quantity, currentProduct) => {
                                      if (
                                        currentProduct.categoriesProduct ===
                                        category.id
                                      ) {
                                        return quantity + 1;
                                      }
                                      return quantity;
                                    },
                                    0
                                  )}{" "}
                                  sản phẩm
                                </p>
                              </div>
                            </div>
                          </td>
                          <td className="p-2 align-middle  bg-transparent border-b whitespace-nowrap shadow-transparent text-center">
                            <span className="text-center">
                              {category.nameCategory}
                            </span>
                          </td>
                          <td className="p-2 leading-normal text-center align-middle bg-transparent border-b whitespace-nowrap shadow-transparent">
                            <span>
                              {category.descriptionCategory || "Chưa có mô tả."}
                            </span>
                          </td>
                          <td className="p-2 align-middle bg-transparent border-b whitespace-nowrap shadow-transparent text-center">
                            <span className="cursor-pointer text-blue-500">
                              Edit
                            </span>{" "}
                            /{" "}
                            <span className="cursor-pointer text-red-500">
                              Xóa
                            </span>
                          </td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
      <div
        className={`w-screen h-screen fixed top-0 left-0 z-30 popupContainer flex justify-center items-center ${
          isShowPopupAdd ? "show-popup" : ""
        }`}
        onClick={hanleTogglePopupAdd}
      >
        <div
          className="w-1/3 min-h-1/2 rounded-lg bg-white p-4 flex flex-col gap-4"
          onClick={(e) => {
            e.stopPropagation();
          }}
        >
          <div className="flex justify-between items-center bg-blue-200">
            <h1 className="font-semibold">Thêm Danh Mục</h1>
            <span
              className="cursor-pointer text-[25px] text-pink-500"
              onClick={hanleTogglePopupAdd}
            >
              <ion-icon name="close-outline"></ion-icon>
            </span>
          </div>
          <div className="w-full min-h-[300px] flex flex-col gap-3 bg-green-100"></div>
        </div>
      </div>
    </>
  );
};

export default Categories;
