import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { fetchCategories } from "../../redux/slices/categoriesSlice";

const Categories = () => {
  const dispatch = useDispatch();
  const categories = useSelector((state) => state.categories.categories);
  useEffect(() => {
    const fectedData = async () => {
      await dispatch(fetchCategories());
    };
    fectedData();
  }, []);

  const [projectForm, setProjectForm] = useState({
    nameProject: "",
    managerProject: "",
    descriptionProject: "",
    startDate: "",
    endDate: "",
  });
  const [projectExits, setProjectExits] = useState(false);
  const data = {
    personnel: [
      { id: 1, name: "John Doe" },
      { id: 2, name: "Jane Smith" },
    ],
  };

  const showHiddenAdd = () => {
    setShowPopup(!showPopup);
  };

  const filterData = () => {
    // Logic to filter data based on searchTerm
  };

  const getNameManager = (id) => {
    const manager = data.personnel.find((person) => person.id === id);
    return manager ? manager.name : "Unknown";
  };

  const deleteProject = (id) => {
    // Logic to delete a project
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    // Logic to handle form submission
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setProjectForm({
      ...projectForm,
      [name]: value,
    });
  };

  return (
    <div
      className="w-full bg-[#e3eaef] rounded-xl p-4 gap-4 flex flex-col"
      id="containerTask"
    >
      <section className="flex flex-col gap-4 overflow-hidden rounded-xl">
        <div className="w-full h-11 flex justify-between items-center">
          <span className="text-2xl text-black font-bold">Dự án</span>
          <div className="flex items-center h-full gap-3">
            <div className="h-full flex items-center relative">
              <ion-icon
                name="search-outline"
                className="text-black absolute left-3"
              ></ion-icon>
              <input
                type="text"
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                onKeyUp={filterData}
                placeholder="Tìm kiếm"
                className="w-full h-full text-black"
                id="search"
              />
            </div>
            <button
              className="h-full bg-[#282853] rounded-xl text-white px-3 flex items-center"
              onClick={showHiddenAdd}
            >
              <ion-icon name="add-outline"></ion-icon>
              <span>Thêm dự án</span>
            </button>
          </div>
        </div>
        <div
          className="w-full bg-white rounded-xl shadow-lg overflow-y-auto overflow-x-hidden p-[19px]"
          id="contentShow"
        >
          {filteredData.length === 0 ? (
            <h1 className="text-black font-semibold">Chưa có dự án!</h1>
          ) : (
            <div className="w-full h-auto grid grid-cols-3 gap-4" id="cards">
              {filteredData.map((item) => (
                <div
                  className="cursor-pointer hover:scale-110 bg-[#ecf0ff] transition duration-400 ease-in-out cardDetail overflow-hidden relative"
                  id="cardProject"
                  key={item.id}
                >
                  <div className="flex flex-col gap-2">
                    <h1 className="text-[1.5em] text-[#6384c2] font-semibold">
                      {item.name}
                    </h1>
                    {item.progress === 1 && (
                      <span className="text-[1em] text-red-500 font-medium">
                        Tiến đô: Đã giao{" "}
                        <ion-icon name="flag-outline"></ion-icon>
                      </span>
                    )}
                    {item.progress === 2 && (
                      <span className="text-[1em] text-green-500 font-medium">
                        Tiến đô: Đã hoàn thành{" "}
                        <ion-icon name="checkmark-done-outline"></ion-icon>
                      </span>
                    )}
                    <span className="text-[1em] text-red-400 font-medium">
                      Quản lí: {getNameManager(item.manager)}.
                    </span>
                    <span className="text-[0.9em] text-[#425475]">
                      Thời gian bắt đầu:{" "}
                      {new Date(item.date_start).toLocaleDateString("vi-VN")}.
                    </span>
                    <span className="text-[0.9em] text-[#425475]">
                      Thời hạn:{" "}
                      {new Date(item.date).toLocaleDateString("vi-VN")}.
                    </span>
                  </div>
                  <button
                    className="group flex justify-center p-2 rounded-md drop-shadow-xl bg-[#ea4c89] from-gray-800 text-white font-semibold hover:translate-y-3 hover:rounded-[50%] transition-all duration-500 hover:from-[#331029] hover:to-[#310413] absolute right-3 bottom-4 h-auto"
                    onClick={() => deleteProject(item.id)}
                  >
                    <ion-icon name="trash-outline"></ion-icon>
                    <span className="absolute opacity-0 group-hover:opacity-100 group-hover:text-[#ea4c89] group-hover:text-sm group-hover:-translate-y-7 duration-700">
                      Xóa
                    </span>
                  </button>
                </div>
              ))}
            </div>
          )}
        </div>
      </section>

      {showPopup && (
        <section
          className="fixed w-full h-full top-0 left-0 z-50 flex justify-center items-center"
          id="sectionPopup"
        >
          <div
            className="w-[35%] h-auto bg-white rounded-xl flex flex-col gap-4 translate-y-[200%] formAddProject"
            id="popUpContent"
          >
            <div className="flex justify-between items-center p-3">
              <span className="text-[#141438] text-2xl font-semibold">
                Thêm dự án mới
              </span>
              <button onClick={showHiddenAdd}>
                <ion-icon
                  name="close-outline"
                  className="text-[#141438] text-3xl font-semibold cursor-pointer"
                ></ion-icon>
              </button>
            </div>
            <form onSubmit={handleSubmit}>
              <span
                className="text-red-700 pl-4 font-semibold"
                id="message__info"
              ></span>
              <div className="flex gap-6 p-4">
                <div className="flex flex-col gap-4 w-2/3">
                  <div className="flex flex-col gap-1">
                    <label
                      htmlFor="project"
                      className="text-black font-semibold text-sm"
                    >
                      Tên dự án
                    </label>
                    <input
                      type="text"
                      id="inputNameProject"
                      name="nameProject"
                      value={projectForm.nameProject}
                      onChange={handleInputChange}
                      placeholder="Tên dự án"
                      className="text-black border border-black rounded-xl py-3 px-6"
                    />
                    {projectExits && (
                      <span className="text-red-700 text-[13px]">
                        Dự án đã tồn tại!
                      </span>
                    )}
                  </div>
                  <div className="flex flex-col gap-1">
                    <label
                      htmlFor="priority"
                      className="text-black font-semibold text-sm"
                    >
                      Người phụ trách
                    </label>
                    <select
                      name="managerProject"
                      value={projectForm.managerProject}
                      onChange={handleInputChange}
                      className="text-black border border-black rounded-lg p-2"
                    >
                      {data.personnel.map((item) => (
                        <option key={item.id} value={item.id}>
                          {item.name}
                        </option>
                      ))}
                    </select>
                  </div>
                  <div className="flex flex-col gap-1">
                    <label
                      htmlFor="taskdecs"
                      className="text-black font-semibold text-sm"
                    >
                      Mô tả
                    </label>
                    <textarea
                      name="descriptionProject"
                      value={projectForm.descriptionProject}
                      onChange={handleInputChange}
                      cols="12"
                      className="text-black border border-black rounded-xl py-3 px-6 resize-none h-24"
                      placeholder="Mô tả dự án"
                    ></textarea>
                  </div>
                </div>
                <div className="flex flex-col gap-4 w-1/3 justify-between">
                  <div className="flex flex-col gap-1">
                    <label
                      htmlFor="startDate"
                      className="text-black font-semibold text-sm"
                    >
                      Ngày bắt đầu
                    </label>
                    <input
                      type="date"
                      name="startDate"
                      value={projectForm.startDate}
                      onChange={handleInputChange}
                      className="text-black border border-black rounded-lg p-2"
                    />
                  </div>
                  <div className="flex flex-col gap-1">
                    <label
                      htmlFor="endDate"
                      className="text-black font-semibold text-sm"
                    >
                      Ngày kết thúc
                    </label>
                    <input
                      type="date"
                      name="endDate"
                      value={projectForm.endDate}
                      onChange={handleInputChange}
                      className="text-black border border-black rounded-lg p-2"
                    />
                  </div>
                  <div>
                    <button
                      type="submit"
                      className="bg-[#282853] text-white p-3 rounded-lg w-full"
                    >
                      Thêm dự án
                    </button>
                  </div>
                </div>
              </div>
            </form>
          </div>
        </section>
      )}
    </div>
  );
};

export default Categories;
