import React, { useState, forwardRef, useImperativeHandle } from "react";
import "./Input.css";

const InputEmaiComponent = forwardRef((props, ref) => {
  const [email, setEmail] = useState("");
  const [emailError, setEmailError] = useState({status: false});

  const handleEmailBlur = (e) => {
    const emailValue = e.target.value;
    setEmail(emailValue);
    const emailRegex = /^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/;
    if (emailValue === "") {
      setEmailError({ status: false, message: "Vui lòng điền địa chỉ email!" });
    } else if (!emailRegex.test(emailValue)) {
      setEmailError({ status: false, message: "Địa chỉ email không hợp lệ!" });
    } else {
      setEmailError({ status: true, message: "" });
    }
  };
  const handleEmailChange = (e) => {
    setEmail(e.target.value);
    setEmailError({ status: true, message: "" });
  };
  useImperativeHandle(ref, () => ({
    value: emailError,
  }));
  return (
    <div
      className={`form__group flex flex-col gap-2 relative ${
        !emailError.status ? "formError" : ""
      }`}
    >
      <input
        type="text"
        name="email"
        value={email}
        onBlur={handleEmailBlur}
        onInput={handleEmailChange}
        onChange={props.handleFormLogin}
        className="w-full input focus:outline-none 
        transition-colors duration-300 focus:border-[#008080]"
        placeholder="Example@gmail.com"
      />
      <span className="text-sm text-red-800">{emailError.message}</span>
      <ion-icon name="mail-outline" />
    </div>
  );
});

export default InputEmaiComponent;
