export function filter(event) {
  const main_filter = document
    .querySelector(".container__filter--main")
    .classList.toggle("show");
  console.log(event.target);
  if (event.target.querySelector("span").innerText === "Hiện filter") {
    event.target.querySelector("span").innerText = "Ẩn filter";
  } else {
    event.target.querySelector("span").innerText = "Hiện filter";
  }
}
