import React, { useState } from "react";
import { useForm } from "react-hook-form";
import { yupResolver } from "@hookform/resolvers/yup";
import * as yup from "yup";
import "./Style.css";
import InputNameComponent from "../InputHookComponents/InputNameComponent";

const schemaValidation = yup
  .object({
    nameCategory: yup
      .string()
      .required("Vui lòng điền thông tin!")
      .max(255, "Không dài quá 255 kí tự!"),
  })
  .required();

const CreateCategoriesForm = () => {
  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm({ resolver: yupResolver(schemaValidation) });
  const onSubmit = (data) => console.log(data);
  return (
    <form
      onSubmit={handleSubmit(onSubmit)}
      className="w-full h-auto bg-white flex flex-col gap-3"
    >
      <div className="form__groupFile flex items-center gap-3">
        <div className="w-14 h-14 rounded-lg flex-shrink-0 overflow-hidden">
          <img
            className="w-full h-full object-cover"
            src="https://cdn4.iconfinder.com/data/icons/picture-sharing-sites/32/No_Image-1024.png"
            alt=""
          />
        </div>
        <input
          className="w-[126px]"
          type="file"
          {...register("imageCategory")}
        />
        <button className="h-[40px] px-7 rounded-lg text-sm text-red-500">
          Xóa
        </button>
      </div>
      {/* <div className="flex flex-col gap-2">
        <label htmlFor="nameCategory" className="text-[15px] font-semibold">
          Tên danh mục
        </label>
        <input
          name="nameCategory"
          type="text"
          className={`pl-8.75 text-sm focus:shadow-soft-primary-outline ease-soft w-1/100 leading-5.6 relative -ml-px block min-w-0 flex-auto rounded-lg border border-solid border-gray-300 bg-white bg-clip-padding pr-3 text-gray-700 transition-all placeholder:text-gray-500 focus:border-[#00d8be] focus:outline-none focus:transition-shadow py-3 px-5 ${
            errors?.nameCategory?.message && "formError"
          }`}
          placeholder="Tên danh mục..."
          fdprocessedid="0v423v"
          {...register("nameCategory")}
        />
        {errors?.nameCategory?.message && (
          <span className="text-sm text-[#dc362e] font-semibold">
            {errors.nameCategory.message}
          </span>
        )}
      </div> */}
      <div className="flex flex-col gap-2">
        <label
          htmlFor="descriptionCategory"
          className="text-[15px] font-semibold"
        >
          Mô tả danh mục
        </label>
        <textarea
          className="pl-8.75 text-sm focus:shadow-soft-primary-outline ease-soft w-1/100 leading-5.6 relative -ml-px block min-w-0 flex-auto rounded-lg border border-solid border-gray-300 bg-white bg-clip-padding pr-3 text-gray-700 transition-all placeholder:text-gray-500 focus:border-[#00d8be] focus:outline-none focus:transition-shadow py-3 px-5 resize-none"
          name="descriptionCategory"
          placeholder="Mô tả danh mục..."
          fdprocessedid="0v423v"
          {...register("descriptionCategory")}
        ></textarea>
        <span className="text-sm text-[#dc362e] font-semibold"></span>
      </div>
      <div className="w-full h-auto flex items-center gap-3">
        <button
          onClick={handleSubmit(onSubmit)}
          className="inline-block w-full px-6 py-3 font-bold text-center uppercase align-middle transition-all bg-transparent border border-solid rounded-lg shadow-none cursor-pointer active:shadow-soft-xs hover:scale-102 active:opacity-85 leading-pro text-xs ease-soft-in tracking-tight-soft bg-150 bg-x-25 border-slate-700 text-slate-700 hover:bg-transparent hover:text-slate-700 hover:shadow-none active:bg-slate-700 active:text-white active:hover:bg-transparent active:hover:text-slate-700 active:hover:shadow-none"
        >
          Thêm Danh Mục
        </button>
        <button className="inline-block px-6 py-3 font-bold text-center text-white uppercase align-middle transition-all bg-transparent border-0 rounded-lg cursor-pointer leading-pro text-xs ease-soft-in hover:shadow-soft-xs hover:scale-102 active:opacity-85 tracking-tight-soft shadow-soft-md bg-150 bg-x-25 bg-gradient-to-tl from-gray-900 to-slate-800 w-2/4">
          Hủy
        </button>
      </div>
    </form>
  );
};

export default CreateCategoriesForm;
