import { createSlice } from "@reduxjs/toolkit";
const initialState = 0;

const cartSlice = createSlice({
  name: "cart",
  initialState,
  reducers: {
    addTo: (state, action) => {
      return state + 1;
    },
  },
});

export const { increment } = cartSlice.actions;
export default cartSlice.reducer;
