const validate = (values, listNameCategories) => {
  console.log("values", listNameCategories);
  const errors = {};
  if (values.nameCategory)
    if (!listNameCategories.includes(values.nameCategory)) {
      errors.nameCategory = "Vui lòng điền tên!";
    } else if (values.name.length > 255) {
      errors.name = "Không quá 255 kí tự!";
    }
  if (!values.name) {
    errors.name = "Vui lòng điền tên!";
  } else if (values.name.length > 255) {
    errors.name = "Không quá 255 kí tự!";
  }
  if (!values.description) {
    errors.description = "Vui lòng mô tả!";
  } else if (values.description.length > 255) {
    errors.description = "Không quá 255 kí tự!";
  }
  return errors;
};
export default validate;
