import React, { useState, forwardRef, useImperativeHandle } from "react";
import "./Input.css";

const InputNameComponent = forwardRef((pro) => {
  const [name, setName] = useState("");
  const [nameError, setNameError] = useState("");
  useImperativeHandle(ref, () => ({
    inputEmailError: email === "" ? true : emailError.status,
  }));
  const handleNameBlur = (event) => {
    const nameValue = event.target.value;
    if (!nameValue) {
      setNameError("Vui lòng điền tên!");
    } else {
      setNameError("");
    }
  };
  const handleNameChange = (event) => {
    setName(event.target.value);
    setNameError("");
  };
  return (
    <div
      className={`form__group flex flex-col gap-2 relative ${
        nameError ? "formError" : ""
      }`}
    >
      <input
        type="text"
        name="name"
        value={name}
        onBlur={handleNameBlur}
        onChange={handleNameChange}
        className="w-full input focus:outline-none 
        transition-colors duration-300 focus:border-[#008080]"
        placeholder="Username"
      />
      <span className="text-sm text-red-800">{nameError}</span>
      <ion-icon name="person-outline"></ion-icon>
    </div>
  );
});

export default InputNameComponent;
