import React, { useState } from "react";
import "./Input.css";

const InputEmaiComponent = () => {
  const [email, setEmail] = useState("");
  const [emailError, setEmailError] = useState("");

  const handleEmailBlur = (e) => {
    const emailValue = e.target.value;
    setEmail(emailValue);
    const emailRegex = /^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/;
    if (!emailRegex.test(emailValue)) {
      setEmailError("Địa chỉ email không hợp lệ!");
    } else {
      setEmailError("");
    }
  };
  const handleEmailChange = () => {
    
    setEmailError("");
  };
  return (
    <div className="form__group flex flex-col gap-2 relative">
      <input
        type="text"
        name="email"
        value={email}
        onBlur={handleEmailBlur}
        onChange={handleEmailChange}
        className="w-full input focus:outline-none 
        transition-colors duration-300 focus:border-[#008080]"
        placeholder="Example@gmail.com"
      />
      <span className="text-sm text-red-800">{emailError}</span>
      <ion-icon name="mail-outline" />
    </div>
  );
};

export default InputEmaiComponent;
