import React, { useState } from "react";

const InputEmaiComponent = () => {
  const [email, setEmail] = useState("");
  const [emailError, setEmailError] = useState("");

  const handleEmailChange = (e) => {
    const emailValue = e.target.value;
    setEmail(emailValue);

    const emailRegex = /^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/;
    if (!emailRegex.test(emailValue)) {
      setEmailError("Please enter a valid email address.");
    } else {
      setEmailError("");
    }
  };
  return (
    <div className="form__group flex flex-col gap-2">
      <input
        type="text"
        name="email"
        className="w-full input focus:outline-none 
        transition-colors duration-300
        border-gray-300 focus:border-blue-ơơ"
        placeholder="Example@gmail.com"
      />
      <span className="text-sm text-red-800">Vui lòng điền thông tin!</span>
    </div>
  );
};

export default InputEmaiComponent;
