import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { fetchCategories } from "../../redux/slices/categoriesSlice";

const Categories = () => {
  const dispatch = useDispatch();
  const categories = useSelector((state) => state.categories.categories);
  useEffect(() => {
    const fectedData = async () => {
      await dispatch(fetchCategories());
    };
    fectedData();
  }, []);

   const [searchTerm, setSearchTerm] = useState('');
  const [filteredData, setFilteredData] = useState([]);
  const [taskForm, setTaskForm] = useState({
    task: '',
    project: '',
    description: '',
    startDate: '',
    endDate: '',
    priority: '',
  });
  const [taskExists, setTaskExists] = useState(false);
  const [showPopup, setShowPopup] = useState(false);
  const [selectedManagerId, setSelectedManagerId] = useState(null);
  const [personnelChecked, setPersonnelChecked] = useState({});

  useEffect(() => {
    setFilteredData(data.tasks);
  }, [data.tasks]);

  const filterData = () => {
    const filtered = data.tasks.filter(task =>
      task.name.toLowerCase().includes(searchTerm.toLowerCase())
    );
    setFilteredData(filtered);
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setTaskForm({ ...taskForm, [name]: value });
  };

  const handleCheckboxChange = (id, isChecked) => {
    setPersonnelChecked({ ...personnelChecked, [id]: isChecked });
  };

  const handleFormSubmit = (e) => {
    e.preventDefault();
    // Implement form submission logic
  };

  const handleUpdateSubmit = () => {
    // Implement update logic
  };

  const handleDelete = () => {
    // Implement delete logic
  };

  const showHiddenAdd = () => {
    setShowPopup(!showPopup);
  };

  return (
    <div className="w-full h-[75px] bg-[#e3eaef] rounded-xl p-4 gap-4 flex flex-col" id="containerTask">
      {showHiddenAdd()}
      <section className="flex flex-col gap-4 overflow-hidden rounded-xl">
        <div className="w-full h-11 flex justify-between items-center">
          <span className="text-2xl text-black font-bold">Tasks</span>
          <div className="flex items-center h-full gap-3">
            <div className="h-full flex items-center relative">
              <IonIcon icon={searchOutline} className="text-black absolute left-3" />
              <input
                type="text"
                placeholder="Tìm kiếm"
                className="w-full h-full text-black"
                id="search"
                value={searchTerm}
                onChange={(e) => {
                  setSearchTerm(e.target.value);
                  filterData();
                }}
              />
            </div>
            <button
              className="h-full bg-[#282853] rounded-xl text-white px-3 flex items-center cursor-pointer"
              onClick={showHiddenAdd}
            >
              <IonIcon icon={addOutline} />
              <span>Thêm task</span>
            </button>
          </div>
        </div>
        <div className="w-full h-[600px] bg-white rounded-xl shadow-lg overflow-y-auto" id="contentShow">
          <table className="w-full table-auto">
            <thead className="">
              <tr className="text-black font-semibold h-[50px]">
                <th className="px-4 py-2 text-left">Mô tả</th>
                <th className="px-4 py-2 text-left">Tiến độ</th>
                <th className="px-4 py-2 text-left">Sự ưu tiên</th>
                <th className="px-4 py-2 text-left">Thời hạn</th>
                <th className="px-4 py-2 text-left">Tên nhiệm vụ</th>
                <th className="px-4 py-2 text-left">Hành động</th>
              </tr>
            </thead>
            <tbody className="">
              {filteredData.length === 0 ? (
                <h1 className="text-black font-semibold ml-4">Chưa có nhiệm vụ!</h1>
              ) : (
                filteredData.map((item) => (
                  <tr className="border-b text-black py-4 h-[40px]" key={item.id}>
                    <td className="px-4 py-2">{item.description}</td>
                    <td className="px-4 py-2 text-blue-500">{item.progress === 1 ? 'Đã giao' : item.progress === 2 ? 'Đang thực hiện' : 'Đã hoàn thành'}</td>
                    <td className="px-4 py-2 text-red-500">{item.priority === 1 ? 'Ưu tiên' : 'Ưu tiên thấp'}</td>
                    <td className="px-4 py-2">{new Date(item.date).toLocaleDateString('vi-VN')}</td>
                    <td className="px-4 py-2">{item.name}</td>
                    <td className="px-4 py-2 cursor-pointer text-red-600" onClick={() => setShowPopup(true)}>
                      <span style={{ display: 'none' }} id="idTask">{item.id}</span>
                      Xem chi tiết
                    </td>
                  </tr>
                ))
              )}
            </tbody>
          </table>
        </div>
      </section>

      {/* popup add */}
      {showPopup && (
        <section className="fixed w-full h-full top-0 left-0 z-50 flex justify-center items-center sectionPopup" id="sectionPopup">
          <div className="w-[35%] h-auto bg-white rounded-xl flex flex-col gap-4 translate-y-[100%]" id="popUpContent">
            <div className="flex justify-between items-center p-3">
              <span className="text-[#141438] text-2xl font-semibold">Thêm nhiệm vụ mới</span>
              <button onClick={() => setShowPopup(false)}>
                <IonIcon icon={closeOutline} className="text-[#141438] text-3xl font-semibold cursor-pointer" />
              </button>
            </div>
            <span className="text-red-700 pl-4 font-semibold" id="message__info"></span>
            <form className="flex gap-6 p-4" onSubmit={handleFormSubmit}>
              <div className="flex flex-col gap-4 w-2/3">
                <div className="flex flex-col gap-1">
                  <label htmlFor="task" className="text-black font-semibold text-sm">Tên nhiệm vụ</label>
                  <input
                    type="text"
                    name="task"
                    value={taskForm.task}
                    onChange={handleInputChange}
                    className="text-black border border-black rounded-xl py-3 px-6"
                    placeholder="Exp: Design Layout"
                    id="nameTaskInput"
                  />
                  <span className="form__message form__message-email text-red-600 font-semibold text-[13px]">
                    {taskExists && 'Nhiệm vụ đã tồn tại!'}
                  </span>
                </div>
                <div className="flex flex-col gap-1">
                  <label htmlFor="project" className="text-black font-semibold text-sm">Thuộc dự án</label>
                  <select
                    name="project"
                    value={taskForm.project}
                    onChange={handleInputChange}
                    className="text-black border border-black rounded-lg p-2"
                  >
                    {data.projects.map((project) => (
                      <option key={project.id} value={project.id}>{project.name}</option>
                    ))}
                  </select>
                </div>
                <div className="flex flex-col gap-1">
                  <label htmlFor="description" className="text-black font-semibold text-sm">Mô tả</label>
                  <textarea
                    name="description"
                    value={taskForm.description}
                    onChange={handleInputChange}
                    cols="12"
                    className="text-black border border-black rounded-xl py-3 px-6 resize-none h-24"
                    placeholder="Mô tả nhiệm vụ"
                  />
                </div>
              </div>
              <div className="flex flex-col gap-4 w-1/3 justify-between">
                <div className="flex flex-col gap-1">
                  <label htmlFor="startDate" className="text-black font-semibold text-sm">Ngày bắt đầu</label>
                  <input
                    type="date"
                    name="startDate"
                    value={taskForm.startDate}
                    onChange={handleInputChange}
                    className="text-black border border-black rounded-lg p-2"
                  />
                </div>
                <div className="flex flex-col gap-1">
                  <label htmlFor="endDate" className="text-black font-semibold text-sm">Ngày kết thúc</label>
                  <input
                    type="date"
                    name="endDate"
                    value={taskForm.endDate}
                    onChange={handleInputChange}
                    className="text-black border border-black rounded-lg p-2"
                  />
                </div>
                <div className="flex flex-col gap-1">
                  <label htmlFor="priority" className="text-black font-semibold text-sm">Mức độ ưu tiên</label>
                  <select
                    name="priority"
                    value={taskForm.priority}
                    onChange={handleInputChange}
                    className="text-black border border-black rounded-lg p-2"
                  >
                    <option value="1">Cao</option>
                    <option value="2">Thấp</option>
                  </select>
                </div>
                <div>
                  <button type="submit" className="bg-[#282853] text-white p-3 rounded-lg w-full">Thêm nhiệm vụ</button>
                </div>
              </div>
            </form>
          </div>
        </section>
      )}

      {/* popup detail */}
      {showPopup && (
        <section className="fixed w-full h-full top-0 left-0 z-50 flex justify-center items-center sectionPopup" id="sectionPopup2">
          <div className="w-[35%] h-auto bg-white rounded-xl flex flex-col gap-4 translate-y-[200%]" id="popUpContent">
            <div className="flex justify-between items-center p-3">
              <span className="text-[#141438] text-2xl font-semibold">Chi tiết nhiệm vụ</span>
              <button onClick={() => setShowPopup(false)}>
                <IonIcon icon={closeOutline} className="text-[#141438] text-3xl font-semibold cursor-pointer" />
              </button>
            </div>
            <span className="text-red-700 pl-4 font-semibold" id="message__infoUpdate"></span>
            <form className="flex gap-6 p-4">
              <div className="flex flex-col gap-4 w-2/3">
                <div className="flex flex-col gap-1">
                  <label htmlFor="task" className="text-black font-semibold text-sm">Tên nhiệm vụ</label>
                  <input
                    type="text"
                    className="pointer-events-none text-black border border-black rounded-xl py-3 px-6"
                    placeholder="Exp: Design Layout"
                    id="nameTaskInputDetail"
                  />
                  <span className="form__message form__message-email text-red-600 font-semibold text-[13px]">
                    {taskExists && 'Nhiệm vụ đã tồn tại!'}
                  </span>
                </div>
                <div className="flex flex-col gap-1">
                  <label htmlFor="project" className="text-black font-semibold text-sm">Thuộc dự án</label>
                  <span className="text-black border border-black rounded-lg p-2">
                    Asm1
                  </span>
                </div>
                <div className="flex flex-col gap-1">
                  <label htmlFor="description" className="text-black font-semibold text-sm">Nhiệm vụ cho</label>
                  <div className="grid grid-cols-2 gap-y-2">
                    {data.personnels.map((item) => (
                      <div className="flex items-center gap-[4px]" key={item.id}>
                        <input
                          type="checkbox"
                          id={item.id}
                          checked={personnelChecked[item.id] || false}
                          onChange={(e) => handleCheckboxChange(item.id, e.target.checked)}
                        />
                        <label htmlFor={item.id} className="text-black text-sm">{item.name}</label>
                      </div>
                    ))}
                  </div>
                </div>
                <div className="flex flex-col gap-1">
                  <label htmlFor="description" className="text-black font-semibold text-sm">Nguời quản lý</label>
                  <div className="grid grid-cols-2 gap-y-2">
                    {data.personnels.map((item) => (
                      <div className="flex items-center gap-[4px]" key={item.id}>
                        <input
                          type="radio"
                          name="manager"
                          id={item.id}
                          value={item.id}
                          checked={selectedManagerId === item.id}
                          onChange={() => setSelectedManagerId(item.id)}
                        />
                        <label htmlFor={item.id} className="text-black text-sm">{item.name}</label>
                      </div>
                    ))}
                  </div>
                  <span className="text-red-700 pl-4 font-semibold" id="message__infoUpdate2"></span>
                </div>
              </div>
              <div className="flex flex-col gap-4 w-1/3 justify-between">
                <div className="flex flex-col gap-1">
                  <label htmlFor="startDate" className="text-black font-semibold text-sm">Ngày bắt đầu</label>
                  <span className="text-black border border-black rounded-lg p-2 pointer-events-none" id="dateStartDetail"></span>
                </div>
                <div className="flex flex-col gap-1">
                  <label htmlFor="endDate" className="text-black font-semibold text-sm">Ngày kết thúc</label>
                  <input
                    type="date"
                    className="text-black border border-black rounded-lg p-2 cursor-pointer"
                    id="date_endDetail"
                  />
                </div>
                <div className="flex flex-col gap-1">
                  <label htmlFor="priority" className="text-black font-semibold text-sm">Mức độ ưu tiên</label>
                  <select className="text-black border border-black rounded-lg p-2" id="priorityUpdate">
                    <option value="1" id="optionDetail">Ưu tiên</option>
                    <option value="2" id="optionDetail">Ưu tiên thấp</option>
                  </select>
                </div>
                <div className="flex flex-col gap-1">
                  <label htmlFor="priority" className="text-black font-semibold text-sm">Tiến độ</label>
                  <select className="text-black border border-black rounded-lg p-2" id="progressUpdate">
                    <option value="1" id="progressOption">Đã giao</option>
                    <option value="2" id="progressOption">Đang thực hiện</option>
                    <option value="3" id="progressOption">Đã hoàn thành</option>
                  </select>
                </div>
                <div className="flex gap-3">
                  <button
                    type="submit"
                    className="bg-[#282853] text-white p-3 rounded-lg w-2/3"
                    onClick={handleUpdateSubmit}
                  >
                    Cập nhật
                  </button>
                  <button
                    type="button"
                    className="bg-red-400 hover:bg-red-500 text-white p-3 rounded-lg w-1/3"
                    onClick={handleDelete}
                  >
                    <span id="idTaskDelete" style={{ display: 'none' }}></span>
                    Xóa
                  </button>
                </div>
              </div>
            </form>
          </div>
        </section>
      )}
    </div>
  );
};

export default Categories;
