import React, { useState } from "react";
import ToastErrorComponent from "./ToastErrorComponent";
import ToastSuccesComponent from "./ToastSuccesComponent";

const ContainerToastComponent = (props) => {
  const [toasts, setToasts] = useState([]);
  setToasts([...toasts, props.toastInfo]);
  props.toastInfo;
  setTimeout(() => {
    removeToast(props.toastInfo.index);
  }, 3000);
   const removeToast = (index) => {
    setToasts((prevToasts) => prevToasts.filter(toast => toast.index !== index));
  };
  return (
    <div>
      <div className="absolute top-[170px] right-0 ">
        {props.toastInfo.map((toast, index) =>
          toast.type === "error" ? (
            <ToastErrorComponent key={index} toastInfo={toast} />
          ) : (
            <ToastSuccesComponent key={index} toastInfo={toast} />
          )
        )}
      </div>
    </div>
  );
};

export default ContainerToastComponent;
