import React, { useState } from "react";

const InputPasswordComponent = (props) => {
  const [password, setPassword] = useState("");
  const [passwordError, setPasswordError] = useState(Ơ);
  const [togglePassword, setTogglePassword] = useState(false);

  const handleTogglePassword = (event) => {
    togglePassword ? setTogglePassword(false) : setTogglePassword(true);
  };
  const handlePasswordChange = (event) => {
    setPassword(event.target.value);
    setPasswordError("");
  };
  const validatePassword = (event) => {
    if (event.target.value.length < 8) {
      setPasswordError("Mật khẩu phải dài hơn 8 kí tự!");
    } else if (event.target.value.length > 35) {
      setPasswordError("Mật khẩu phải ngắn hơn 35 kí tự!");
    } else {
      setPasswordError("");
    }
  };
  return (
    <div
      className={`form__group flex flex-col gap-2 relative ${
        passwordError ? "formError" : ""
      }`}
    >
      <input
        type={togglePassword ? "text" : "password"}
        name="password"
        value={password}
        onBlur={validatePassword}
        onInput={handlePasswordChange}
        onChange={props.handleFormLogin}
        className="w-full input focus:outline-none 
        transition-colors duration-300 focus:border-[#008080]"
        placeholder="Password"
      />
      <span className="text-sm text-red-800">{passwordError}</span>
      <ion-icon
        onClick={handleTogglePassword}
        name={`lock-${togglePassword ? "open" : "closed"}-outline`}
        id="iconPassword"
      ></ion-icon>
    </div>
  );
};

export default InputPasswordComponent;
