import React, { useState, useEffect } from "react";
import ToastErrorComponent from "./ToastErrorComponent";
import ToastSuccessComponent from "./ToastSuccesComponent";

const ContainerToastComponent = ({ toastInfo }) => {
  const [toasts, setToasts] = useState([]);
  console.log(toastInfo);

  useEffect(() => {
    if (toastInfo.length > 0) {
      const newToast = toastInfo[toastInfo.length - 1];
      setToasts((prevToasts) => [...prevToasts, newToast]);
      setTimeout(() => {
        removeToast(newToast.id);
      }, 3.500); // Thời gian hiển thị toast là 3 giây
    }
  }, [toastInfo]);

  const removeToast = (id) => {
    setToasts((prevToasts) => prevToasts.filter((toast) => toast.id !== id));
  };

  return (
    <div className="absolute top-[170px] right-0">
      {toasts.map((toast) =>
        toast.type === "error" ? (
          <ToastErrorComponent key={toast.id} toastInfo={toast} />
        ) : (
          <ToastSuccessComponent key={toast.id} toastInfo={toast} />
        )
      )}
    </div>
  );
};

export default ContainerToastComponent;
