import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";
import axios from "axios";
/** create the thunk */
const API = "http://localhost:3080";
export const fetchCategories = createAsyncThunk(
  "cart/fetchCategories",
  async () => {
    const response = await axios.get(`${API}/categories`);
    return response.data;
  }
);
export const createCategory = createAsyncThunk(
  "cart/createCategory",
  async (categoryData, { rejectWithValue }) => {
    try {
      const formData = new FormData();
      formData.append("nameCategory", categoryData.nameCategory);
      formData.append("descriptionCategory", categoryData.descriptionCategory);
      formData.append("imageCategory", categoryData.imageCategory[0]);
      

      // const response = await axios.post(`${API}/createCategory`, formData, {
      //   headers: {
      //     "Content-Type": "multipart/form-data",
      //   },
      // });
      // return response.data;
    } catch (error) {
      return rejectWithValue(error.response.data);
    }
  }
);
const initialState = {
  categories: [],
  loading: false,
  isError: false,
};

const categoriesSlice = createSlice({
  name: "categories",
  initialState,
  reducers: { hidden: () => {} },
  extraReducers: (builder) => {
    /** fectch data */
    builder.addCase(fetchCategories.fulfilled, (state, action) => {
      state.categories = action.payload;
      state.loading = false;
      state.isError = false;
    });
    builder.addCase(fetchCategories.rejected, (state, action) => {
      state.loading = false;
      state.isError = true;
    });
    /** create new data */
    builder.addCase(createCategory.fulfilled, (state, action) => {
      state.categories = action.payload;
      state.loading = false;
      state.isError = false;
    });
    builder.addCase(createCategory.rejected, (state, action) => {
      state.loading = false;
      state.isError = true;
    });
  },
});

export const { hidden } = categoriesSlice.actions;
export default categoriesSlice.reducer;
