import React, { useContext } from "react";
import { Routes, Route } from "react-router-dom";
import { AccountContext } from "./contexts/AccountContext";
import "./App.css";
import "./assets/styles/index.css";
import "./assets/styles/cart-popup.css";
import "./assets/styles/slickListProducts.css";
import "./assets/js/lazy-load";
import "./assets/js/menu";
import HeaderComponent from "./components/HeaderComponent";
import FooterComponent from "./components/FooterComponent";
import Home from "./pages/Home";
import Product from "./pages/Product";
import Shop from "./pages/Shop";
import Login from "./pages/Login";
import Register from "./pages/Register";
import Dashboard from "./pages/admin/Dashboard";
import Cart from "./pages/Cart";
import PrivateRoutes from "./routes/PrivateRoutes";
import AdminRoutes from "./routes/AdminRoutes";
import AppRoutes from "./routes/AppRoutes";
import { ProductService } from "./services/ProductServices";
function App() {
  const { account, setAccount } = useContext(AccountContext);
  const isAdmin = account?.dataJwt?.role === "admin";
  const isLogin = account;
  console.log(isAdmin, isLogin);
  if (isAdmin) {
    return <></>;
  }
  return (
    <>
      <HeaderComponent></HeaderComponent>
      <div className="">
        <Routes>
          <Route path="/" element={<Home></Home>} />
          <Route path="/product/:idCate/:id" element={<Product></Product>} />
          <Route path="/shop" element={<Shop></Shop>} />
          <Route path="/cart" element={<Cart infoUser={account}></Cart>} />
          <Route
            path="/login"
            element={
              <PrivateRoutes isLogin={isLogin}>
                <Login></Login>
              </PrivateRoutes>
            }
          />
          <Route
            path="/register"
            element={
              <PrivateRoutes isLogin={isLogin}>
                <Register></Register>
              </PrivateRoutes>
            }
          />
          {/* Routes Admin */}
          <Route
            path="/dashboard"
            element={<AdminRoutes isAdmin={isAdmin}></AdminRoutes>}
          />
          <Route path="*" element={<Home></Home>} />
        </Routes>
      </div>
      <FooterComponent></FooterComponent>
    </>
  );
}

export default App;
