import React, { useState, useRef, useContext } from "react";
import { Link, useNavigate } from "react-router-dom";
import Cookies from "js-cookie";
import "../assets/styles/login.css";
import { loginAccount } from "../services/AuthServices";
import InputEmaiComponent from "../components/InputComponents/InputEmaiComponent";
import InputPasswordComponent from "../components/InputComponents/InputPasswordComponent";
import ContainerToastComponent from "../components/ToastMessageComponents/ContainerToastComponent";
import { AccountContext } from "../contexts/AccountContext";

const Login = () => {
  const navigate = useNavigate();
  const emailInputRef = useRef(null);
  const passwordInputRef = useRef(null);
  const [valueLogin, setValueLogin] = useState({}); // set value form login
  const [statusForm, setStatusForm] = useState({}); // set value form status
  const [toasts, setToasts] = useState([]);
  const handleFormLogin = (event) => {
    setValueLogin({
      ...valueLogin,
      [event.target.name]: event.target.value,
    });
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    const emailComponentValue = emailInputRef.current;
    const passwordComponentValue = passwordInputRef.current;
    setStatusForm({
      emailStatus: emailComponentValue.inputEmailError,
      passwordStatus: passwordComponentValue.inputPasswordError,
    });
    if (!valueLogin.email || !valueLogin.password) {
      // trường hợp form không điền thông tin
      setToasts([
        ...toasts,
        {
          id: Date.now(),
          type: "error",
          title: "Vui lòng thử lại",
          message: "Vui lòng điền đủ thông tin!",
        },
      ]);
    } else if (statusForm.emailStatus || statusForm.passwordStatus) {
      // trường hợp input không đúng định dạng
      setToasts([
        ...toasts,
        {
          id: Date.now(),
          type: "error",
          title: "Vui lòng thử lại",
          message: "Vui lòng điền thông tin hợp lệ!",
        },
      ]);
    } else {
      const response = await loginAccount(valueLogin);
      if (response.status === 404) {
        setToasts([
          ...toasts,
          {
            id: Date.now(),
            type: "error",
            title: response.title,
            message: response.message,
          },
        ]);
      } else if (response.status === 201) {
        Cookies.set("jwt", response.data, { expires: 1 });
        setToasts([
          ...toasts,
          {
            id: Date.now(),
            type: "success",
            title: "Thành công",
            message: "Đăng nhập thành công!",
          },
        ]);
        setTimeout(() => {
          navigate("/");
        }, 1000);
      }
    }
  };

  return (
    <div className="overflow-hidden">
      <div className="container__main">
        <div className="form-container">
          <p className="title">Welcome back</p>
          <form className="form">
            <InputEmaiComponent
              handleForm={handleFormLogin}
              ref={emailInputRef}
            ></InputEmaiComponent>
            <InputPasswordComponent
              ref={passwordInputRef}
              handleForm={handleFormLogin}
            ></InputPasswordComponent>
            <p className="page-link">
              <span className="page-link-label">Forgot Password?</span>
            </p>
            <button className="form-btn" onClick={handleSubmit}>
              Log in
            </button>
          </form>
          <p className="sign-up-label font-14">
            Don't have an account?
            <Link to={"/register"} className="sign-up-link font-14">
              Sign up
            </Link>
          </p>
          <div className="buttons-container">
            <div className="apple-login-button">
              <svg
                stroke="currentColor"
                fill="currentColor"
                strokeWidth={0}
                className="apple-icon"
                viewBox="0 0 1024 1024"
                height="1em"
                width="1em"
                xmlns="http://www.w3.org/2000/svg"
              >
                <path d="M747.4 535.7c-.4-68.2 30.5-119.6 92.9-157.5-34.9-50-87.7-77.5-157.3-82.8-65.9-5.2-138 38.4-164.4 38.4-27.9 0-91.7-36.6-141.9-36.6C273.1 298.8 163 379.8 163 544.6c0 48.7 8.9 99 26.7 150.8 23.8 68.2 109.6 235.3 199.1 232.6 46.8-1.1 79.9-33.2 140.8-33.2 59.1 0 89.7 33.2 141.9 33.2 90.3-1.3 167.9-153.2 190.5-221.6-121.1-57.1-114.6-167.2-114.6-170.7zm-105.1-305c50.7-60.2 46.1-115 44.6-134.7-44.8 2.6-96.6 30.5-126.1 64.8-32.5 36.8-51.6 82.3-47.5 133.6 48.4 3.7 92.6-21.2 129-63.7z" />
              </svg>
              <span>Log in with Apple</span>
            </div>
            <div className="google-login-button">
              <svg
                stroke="currentColor"
                fill="currentColor"
                strokeWidth={0}
                version="1.1"
                x="0px"
                y="0px"
                className="google-icon"
                viewBox="0 0 48 48"
                height="1em"
                width="1em"
                xmlns="http://www.w3.org/2000/svg"
              >
                <path
                  fill="#FFC107"
                  d="M43.611,20.083H42V20H24v8h11.303c-1.649,4.657-6.08,8-11.303,8c-6.627,0-12-5.373-12-12
	c0-6.627,5.373-12,12-12c3.059,0,5.842,1.154,7.961,3.039l5.657-5.657C34.046,6.053,29.268,4,24,4C12.955,4,4,12.955,4,24
	c0,11.045,8.955,20,20,20c11.045,0,20-8.955,20-20C44,22.659,43.862,21.35,43.611,20.083z"
                />
                <path
                  fill="#FF3D00"
                  d="M6.306,14.691l6.571,4.819C14.655,15.108,18.961,12,24,12c3.059,0,5.842,1.154,7.961,3.039l5.657-5.657
	C34.046,6.053,29.268,4,24,4C16.318,4,9.656,8.337,6.306,14.691z"
                />
                <path
                  fill="#4CAF50"
                  d="M24,44c5.166,0,9.86-1.977,13.409-5.192l-6.19-5.238C29.211,35.091,26.715,36,24,36
	c-5.202,0-9.619-3.317-11.283-7.946l-6.522,5.025C9.505,39.556,16.227,44,24,44z"
                />
                <path
                  fill="#1976D2"
                  d="M43.611,20.083H42V20H24v8h11.303c-0.792,2.237-2.231,4.166-4.087,5.571
	c0.001-0.001,0.002-0.001,0.003-0.002l6.19,5.238C36.971,39.205,44,34,44,24C44,22.659,43.862,21.35,43.611,20.083z"
                />
              </svg>
              <span>Log in with Google</span>
            </div>
          </div>
        </div>
      </div>
      <ContainerToastComponent toastInfo={toasts}></ContainerToastComponent>
    </div>
  );
};

export default Login;
