import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";
import axios from "axios";
const API = "http://localhost:3080";
/** create the thunk */
export const fetchProducts = createAsyncThunk(
  "cart/fetchProducts",
  async () => {
    const response = await axios.get(`http://localhost:3080/products`);
    return response.data;
  }
);
export const createProduct = createAsyncThunk(
  "cart/createProduct",
  async (categoryData, { rejectWithValue }) => {
    try {
      const formData = new FormData();
      formData.append("nameProduct", categoryData.nameProduct);
      formData.append("descriptionProduct", categoryData.descriptionProduct);
      formData.append("category", categoryData.category);
      formData.append("imageProduct", categoryData.imageProduct[0]);
      for (let i = 0; i < categoryData.imageDetailProduct.length; i++) {
        console.log(i);
        formData.append(
          "imageDetailProduct",
          categoryData.imageDetailProduct[i]
        );
      }
      // await axios.post(`${API}/createProduct`, formData, {
      //   headers: {
      //     "Content-Type": "multipart/form-data",
      //   },
      // });
      // const response = await axios.get(`${API}/products`);
      // return response.data;
    } catch (error) {
      return rejectWithValue(error.response.data);
    }
  }
);
const initialState = {
  products: [],
  loading: false,
  isError: false,
};

const productsSlice = createSlice({
  name: "products",
  initialState,
  reducers: {
    hidden: () => {},
  },
  extraReducers: (builder) => {
    builder.addCase(fetchProducts.pending, (state, action) => {
      state.loading = true;
      state.isError = false;
    });
    builder.addCase(fetchProducts.fulfilled, (state, action) => {
      state.products = action.payload;
      state.loading = false;
      state.isError = false;
    });
    builder.addCase(fetchProducts.rejected, (state, action) => {
      state.loading = false;
      state.isError = true;
    });
    /** */
  },
});

export const { hidden } = productsSlice.actions;
export default productsSlice.reducer;
